package ru.ivanovaanastasiya.lesson_2;/*1. Напишите метод, на вход которого подается двумерный строковый массив размером 4х4,при подаче массива другого размера необходимо бросить исключение MyArraySizeException.2. Далее метод должен пройтись по всем элементам массива, преобразовать в int, и просуммировать. Если в каком-тоэлементе массива преобразование не удалось (например, в ячейке лежит символ или текст вместо числа), должно бытьброшено исключение MyArrayDataException – с детализацией, в какой именно ячейке лежат неверные данные.3. В методе main() вызвать полученный метод, обработать возможные исключения MySizeArrayException иMyArrayDataException и вывести результат расчета. */public class HomeWorkException {    public static void main(String[] args) {        String[][] arrStr = {{"1", "3", "1", "2"},                             {"2", "3", "2", "2"},                             {"5", "6", "7", "1"},                             {"3", "3", "1", "0"}};        int sum;        try {            sum = myException(arrStr);            System.out.println(sum);        } catch (MyArraySizeException e) {            System.out.println("Ошибка \"Неправильный размер массива\" в строке: " + (e.getNumColm() + 1)                    + ", в столбце: " + (e.getNumRows() + 1));        } catch (MyArrayDataException e) {            System.out.println("Ошибка \"Неправильный элемент массива\" в строке: " + (e.getNumColm() + 1)                    + ", в столбце: " + (e.getNumRows() + 1));        }    }    static int myException(String[][] arrStr) {        int len1 = arrStr.length;        int len2 = 0;        for (int i = 0; i < arrStr.length; i++) {            len2 = arrStr[i].length;            if (len1 != 4 || len2 != 4) {                throw new MyArraySizeException(i, arrStr.length);            }        }        int result = 0;        int[][] arrInt = new int[4][4];        for (int i = 0; i < arrStr.length; i++) {            for (int j = 0; j < arrStr[i].length; j++) {                try {                    arrInt[i][j] = Integer.parseInt(arrStr[i][j]);                } catch (NumberFormatException e) {                    throw new MyArrayDataException(i, j);                }                result += arrInt[i][j];            }        }        return result;    }}