package ru.ivanovaanastasiya.lesson_2;public class MyArrayDataException extends NumberFormatException {    private int numColm;    private int numRows;    MyArrayDataException(int numColm, int numRows) {        super();        this.numColm = numColm;        this.numRows = numRows;    }    public int getNumColm() {        return numColm;    }    public int getNumRows() {        return numRows;    }}