package ru.ivanovaanastasiya.lesson_2;public class MyArraySizeException extends ArrayIndexOutOfBoundsException{    private int numColm;    private int numRows;    MyArraySizeException(int numColm, int numRows) {        super();        this.numColm = numColm;        this.numRows = numRows;    }    public int getNumColm() {        return numColm;    }    public int getNumRows() {        return numRows;    }}